import React from 'react'
import './Profile.css'
import search from '../images/search.svg'
import { useState } from 'react'

export default function Profile() {
  const [inputBox, setInput] = useState('')
  const [info, setInfo] = useState('')

  const getProfile = () => {
    if (!inputBox) return console.log('missing input')
    console.log('breakpoint')
    fetch(`//localhost:3001/linkedin?uname=${inputBox}`, {
      method: 'POST'
    }).then((data) => {
      console.log(data)
    })
  }

  return (
    <section class="browser">
      <h1>What info your Internet Profiles have on you? </h1>
      <div class="search-row">
        <input
          type="text"
          value={inputBox}
          class="profile-search"
          placeholder="Enter username"
          onChange={(e) => setInput(e.target.value)}
        />
        <button class="search-icon" onClick={() => getProfile()}>
          <img src={search} alt="search-ico" />
        </button>
      </div>

      {info.length >= 1 && (
        <div class="profile-content">
          <p class="upper-light">PERSONAL INFORMATION</p>
          <img src={info.profile.imageurl} alt="dp" />
        </div>
      )}
    </section>
  )
}
