import React from 'react'
import './Profile.css'
import search from '../images/search.svg'
import axios from 'axios'
import { useState } from 'react'

export default function Profile() {
  const [inputBox, setInput] = useState('')
  const [info, setInfo] = useState('')
  const [show, toggleShow] = useState(false)

  const getProfile = () => {
    if (!inputBox) return console.log('missing input')
    console.log('breakpoint')
    axios
      .get(`//localhost:3001/linkedin?uname=${inputBox}`)
      .then(function(response) {
        // handle success
        console.log(response)
        setInfo(response)
        toggleShow(true)
      })
  }

  return (
    <section class="browser">
      <h1>What info your Internet Profiles have on you? </h1>
      <div class="search-row">
        <input
          type="text"
          value={inputBox}
          class="profile-search"
          placeholder="Enter username"
          onChange={(e) => setInput(e.target.value)}
        />
        <button class="search-icon" onClick={() => getProfile()}>
          <img src={search} alt="search-ico" />
        </button>
      </div>

      {show && (
        <div class="profile-content">
          <p class="upper-light">PERSONAL INFORMATION</p>
          {console.log(info.data.imageurl)}
          <img src={info.data.imageurl} alt="dp" />
        </div>
      )}
    </section>
  )
}
