/* eslint-disable jsx-a11y/alt-text */
import React, { Component } from 'react'
import browserLogo from '../images/browser.svg'
import cog from '../images/cog.svg'
import backwards from '../images/backwards.svg'

function Photo() {
  const [fbflag, setFbFlag] = React.useState(false)



  const browserInfo = () => {
    let sUsrAg = navigator.userAgent
    let sBrowser
    if (sUsrAg.indexOf('Firefox') > -1) {
      sBrowser = 'Mozilla Firefox'
    } else if (sUsrAg.indexOf('SamsungBrowser') > -1) {
      sBrowser = 'Samsung Internet'
    } else if (sUsrAg.indexOf('Opera') > -1 || sUsrAg.indexOf('OPR') > -1) {
      sBrowser = 'Opera'
    } else if (sUsrAg.indexOf('Trident') > -1) {
      sBrowser = 'Microsoft Internet Explorer'
    } else if (sUsrAg.indexOf('Edge') > -1) {
      sBrowser = 'Microsoft Edge'
    } else if (sUsrAg.indexOf('Chrome') > -1) {
      sBrowser = 'Google Chrome/ Chromium'
    } else if (sUsrAg.indexOf('Safari') > -1) {
      sBrowser = 'Apple Safari'
    } else {
      sBrowser = 'unknown'
    }
    return sBrowser
  }

  const osNames = () => {
    let osName = 'Not known'
    if (navigator.appVersion.indexOf('Win') != -1) osName = 'Windows OS'
    if (navigator.appVersion.indexOf('Mac') != -1) osName = 'Mac OS'
    if (navigator.appVersion.indexOf('X11') != -1) osName = 'UNIX '
    if (navigator.appVersion.indexOf('Linux') != -1) osName = 'GNU/ Linux '
    return osName
  }

  return (
    <section class="browser">
      <h1>Reverse Searching Google Images</h1>
      <div class="browser-content">
        <p class="upper-light">
          REVERSE SEARCHING IMAGES ON THE WEB TO FIND AND KNOW IF THE IMAGE IS
          UPLOADED TO A BAD DOMAIN.
        </p>
        <div class="file-row">
          <input class="image" type="file" name="image" />
          <button>Upload</button>
        </div>
      </div>
    </section>
  )
}

export default Photo
