import React from 'react'
import { useState } from 'react'

export default function Photo() {
  const [inputBox, setInput] = useState('')
  const [info, setInfo] = useState('')

  const getProfile = () => {
    if (!inputBox) return console.log('missing input')
    console.log('breakpoint')
    fetch(`//localhost:3001/linkedin?uname=${inputBox}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      }
    })
      .then((data) => data.json())
      .then((d) => console.log(d))
  }

  return (
    <section class="browser">
      <h1>
        Reverse Image search to find if your data is hosted on bad domains
      </h1>
      <input type="file" name="image" class="image" />
      <button class="button" onClick={() => {}}></button>

      {info.length >= 1 && (
        <div class="profile-content">
          <p class="upper-light">PERSONAL INFORMATION</p>
          <img src={info.profile.imageurl} alt="dp" />
        </div>
      )}
    </section>
  )
}
