import React  from 'react'

function Photo() {
  const [states, setStates] = React.useState('')
  const [loader, setLoader] = React.useState(false)

  return (
    <section class="browser">
      <h1>Reverse Searching Google Images</h1>
      <div class="browser-content">
        <p class="upper-light">
          Google Crawler to find domain links of uploaded Images.
        </p>
        <div class="file-row">
          <input
            class="image"
            type="file"
            name="image"
            accept="image/*"
          />
          <button
            onClick={() => {
              setLoader(true)
              let data = new FormData()
              let a = document.querySelector('.image').files[0]
              data.append('image', a)

              fetch('//localhost:3001/google', {
                method: 'POST',
                body: data
              })
                .then((data, err) => data.text())
                .then((data) => {
                  console.log(data)
                  let ar = data.split(',')
                  setStates(ar)
                })
            }}
          >
            Upload
          </button>
        </div>
      </div>
      <div class="google-search-results">
        {states && (
          <>
            <p class="table-title">Domain</p>
            <p className="table-title">Results</p>
          </>
        )}

        {states &&
          states.map((i) => {
            return [<p>{i}</p>, <p>GOOD</p>]
          })}
        {states.length > 1 && <p>No Bad domain found</p>}
      </div>
    </section>
  )
}

export default Photo
